//ADJUNTAR DOCUMENTO ENCUESTA SATISFACCION 

$("#doc_encu").change(function(e) {
		$("#adj_encu").submit();
 });

$("#adj_encu").submit(function(e){

	bloquearPantalla();

	// evito que propague el submit
	e.preventDefault();
		  
	// agrego la data del form a formData
	var form = document.getElementById('adj_encu');
	var formData = new FormData(form);

	$.ajaxSetup({
		headers: {
			'X-CSRF-TOKEN': $('meta[name="_token"]').attr('content')
		}
	});

	$.ajax({
	    type:'POST',
	    data:formData,
	    url: "{{ route('documento.enc.sat.ter') }}",
	    cache:false,
	    contentType: false,
	    processData: false,
	    success:function(resp){

	    	desbloquearPantalla();

	        if (resp.estado == 1){

	        	$("#ter_enc_sat").text(" ");

	        	$("#ter_enc_sat").text(resp.filename);
				// INICIO CZ SPRINT 66
	        	$("#link_inv").attr("href", "../../doc/"+resp.filename+"");
	        	// FIN CZ SPRINT 66
                $("#alert-doc").show();

                 $("#descarga_encu_sat").show();

				//$("#valida_doc").val()=1;
	 			//$("#valida_doc").val(1);
					
				setTimeout(function(){ $("#alert-doc").hide(); }, 7000);

			}else if (resp.estado == 0){
                $("#alert-err-doc").show();
				setTimeout(function(){ $("#alert-error-doc").hide(); }, 7000);
			}
		    
		},
		error: function(jqXHR, text, errors){

			desbloquearPantalla();

			$("#alert-err-doc-ext").show();
			setTimeout(function(){ $("#alert-err-doc-ext").hide(); }, 7000);

		},
		complete: function(resp) {

			desbloquearPantalla();
			//alert("okok");
       		//$("#ter_enc_sat").val("");
   		}
	});


});

/**
 * Función que te redirige a la sección de sesiones de terapia familiar
 */
function sesionTerapiaFamiliar(){
	let planificacion = "?planificacion=0";
	let run = "&run={{ $run }}";
	let ruta = '{{ route("planificarSesionesTerapiaFamiliarNNA")  }}'+planificacion+run;

	window.location.assign(ruta);
}

