<!-- SIGUIENTE ETAPA -->
				
<script type="text/javascript">				

	//ADJUNTAR DOCUMENTO INVITACION

	$("#doc_invit").change(function(e) {
			$("#adj_invit").submit();
	 });

	$("#adj_invit").submit(function(e){

		bloquearPantalla();

		// evito que propague el submit
		e.preventDefault();
			  
		// agrego la data del form a formData
		var form = document.getElementById('adj_invit');
		var formData = new FormData(form);

		$.ajaxSetup({
			headers: {
				'X-CSRF-TOKEN': $('meta[name="_token"]').attr('content')
			}
		});

		$.ajax({
		    type:'POST',
		    data:formData,
		    url: "{{ route('documento.invitacion.terapia') }}",
		    cache:false,
		    contentType: false,
		    processData: false,
		    success:function(resp){
		    	desbloquearPantalla();

		        if (resp.estado == 1){
					//INICIO CZ SPRINT 66 
		        	$("#link_inv").attr("href", "../../doc/"+resp.filename+"");
					// FIN CZ SPRINT 66
	                $("#alert-doc").show();

	                 $("#descarga_invitacion_rechazo").show();
						
					setTimeout(function(){ $("#alert-doc").hide(); }, 10000);

				}else if (resp.estado == 0){
	                $("#alert-err-doc").show();
	                $("#alert-err-doc").text(resp.mensaje);

					setTimeout(function(){ $("#alert-err-doc").hide(); }, 10000);
					setTimeout(function(){  
							$("#alert-err-doc").text('Error al momento de subir el documento. Por favor intente nuevamente.');
						}, 10000);

				}
			},
			error: function(jqXHR, text, errors){
				desbloquearPantalla();
				console.log(jqXHR);

				$("#alert-err-doc").show();
				setTimeout(function(){ $("#alert-err-doc").hide(); }, 10000);
			}
		});

	});

	// FIN DOCUMENTO INVITACION


	/**
	 * Función que te redirige a la sección de sesiones de terapia familiar
	 */
	function sesionTerapiaFamiliar(){
		let planificacion = "?planificacion=0";
		let run = "&run={{ $run }}";
		let ruta = '{{ route("planificarSesionesTerapiaFamiliarNNA")  }}'+planificacion+run;

		window.location.assign(ruta);
	}

</script>
